  ('10','WidgetManager','<b>v 1.3</b> Evolution Dashboard Widget Manager','0','7','0','/**\n * WidgetManager\n *\n * Evolution Dashboard Widget Manager\n *\n * @author    Nicola Lambathakis\n * @category    plugin\n * @version    v 1.3\n * @license    http://www.gnu.org/copyleft/gpl.html GNU Public License (GPL)\n * @internal    @events OnManagerWelcomeHome,OnManagerWelcomeRender\n * @internal    @installset base\n * @internal    @modx_category Dashboard\n * @internal    @properties &welcome_showhide=Show Welcome Widget:;menu;show,hide,AdminOnly;show  &welcome_sizex=Welcome width:;list;12,6,4,3;6 &welcome_menuindex=Welcome sort order:;text;1  &onlineinfo_showhide=Show Online Info widget:;menu;show,hide,AdminOnly;show &onlineinfo_sizex=Online Info width:;list;12,6,4,3;6 &onlineinfo_menuindex=Online Info sort order:;text;2 &recentinfo_showhide=Show Recent resource Widget:;menu;show,hide,AdminOnly;show &recentinfo_sizex=Recent resource width:;list;12,6,4,3;12 &recentinfo_menuindex=Recent resource sort order:;text;3 &news_showhide=Show News Widget:;menu;show,hide,AdminOnly;show &news_sizex=News width:;list;12,6,4,3;6 &news_menuindex=News sort order:;text;4 &security_showhide=Show SecurityNews Widget:;menu;show,hide,AdminOnly;show &security_sizex=SecurityNews width:;list;12,6,4,3;6 &security_menuindex=SecurityNews order:;text;5 &config_showhide=Show Configuration Button:;menu;show,hide;show\n*/\n\n// get language\nglobal $_lang;\n// get plugin id\n$result = $modx->db->select(\'id\', $this->getFullTableName(\"site_plugins\"), \"name=\'{$modx->event->activePlugin}\' AND disabled=0\");\n$pluginid = $modx->db->getValue($result);\n\n// get manager role\n$role = $_SESSION[\'mgrRole\']; 	\n//config button placeholder\n$button_pl_config = \'\';\n//Show Configuration Button\n$config_showhide = isset($config_showhide) ? $config_showhide: \'show\';\nif($modx->hasPermission(\'edit_plugin\') AND $config_showhide == \'show\') {\n	$button_pl_config = \'\n    <p class=\"text-muted pull-