  ('22','isShowTech','','0','14','0','\n$id = isset($id) ? $id : $modx->documentIdentifier;\n$is_show = $modx->documentObject[\'isShowTech\'];\n$parent = (int)$modx->documentObject[\'parent\'];\n$output = \'\';\n$img = \'\';\n$desc = \'\';\nif(is_array($is_show)):\n	$is_show = (int)$is_show[1];\n	if($is_show):\n		$obj = $modx->getTemplateVars(array(12,13,15), \'*\', $parent, 1, \'id\');\n		$img = (string)$obj[0][\'value\'];\n		$desc = (string)$obj[1][\'value\'];\n		if(!empty($img) || !empty($desc)):\n			$output .= \'<div class=\"teacher_title\">\';\n			if(!empty($img)):\n				$src = $modx->runSnippet(\'thumb\', array(\n					\'input\'=>$img,\n					\'options\'=>\'w=500,h=748,zc=C\'\n				));\n				$output .= \'<div class=\"teacher_image\"><img src=\"\' . $src . \'\" alt=\"\"></div>\';\n			else:\n				$output .= \'<span></span>\';\n			endif;\n			$output .= \'<div class=\"teacher_description\"><p class=\"text-italic\">\' . nl2br($desc) . \'</p></div></div>\';\n		endif;\n	endif;\nendif;\nreturn $output;','0','{}',' ','1626007012','1626008775','0'),
  ('23','GetIP','Определение запуска в школе или из вне','0','7','0','\nfunction get_ip() {\n	$ip = $_SERVER[\'REMOTE_ADDR\'] || \"\";\n	$headers = array(\n		\'HTTP_X_FORWARDED_FOR\',\n		\'HTTP_X_CLUSTER_CLIENT_IP\',\n		\'HTTP_CF_CONNECTING_IP\',\n		\'HTTP_CLIENT_IP\',\n		\'HTTP_FORWARDED_FOR\',\n		\'HTTP_X_FORWARDED\',\n		\'HTTP_FORWARDED\',\n		\'HTTP_VIA\',\n		\'HTTP_X_COMING_FROM\',\n		\'HTTP_COMING_FROM\',\n		\'REMOTE_ADDR\'\n	);\n	foreach ( $headers as $header ) {\n		if ( isset( $_SERVER[ $header ] ) ) {\n			$ip = $_SERVER[ $header ];\n		}\n	}\n	$reg = \'/^(80.234.84)(?:.\\d+)$/\';\n	preg_match($reg, $ip, $matches);\n	$obj = new stdClass;\n	$obj->name = $_SERVER[\"SERVER_NAME\"];\n	$obj->stime = $_SERVER[\"REQUEST_TIME\"];\n	$obj->path = $_SERVER[\"PATH_INFO\"];\n	$obj->int = 0;\n	if(isset($matches[1])){\n		$obj->int = 1;\n	}\n	return json_encode($obj, JSON_UNESCAPED_SLASHES);\n}\n\nreturn get_ip();','0','{}',' ','1635504406','1635505522','0'),
